# 1) crea il seguente array composto di 2 righe, 3 colonne, 4 profonditàarray_1 <- array(1:24, dim = c(2, 3, 4))# accedi all'elemento della seconda riga, prima colonna, quarta profonditàarray_1[2, 1, 4]# 2) moltiplica tutti gli elementi della seconda dimensione per 2 e stampa l'array modificatoprint(array_1)array_1[, , 2] <- array_1[, , 2] * 2print(array_1)# 3) dato l'array casuale creato col codice seguente, ordina l'array# dal valore più grande al più piccolo con la funzione sort()set.seed(12345)array_2 <- array(sample(1:200, 24, replace = TRUE), dim = c(2, 3, 4))sort(array_2, decreasing = T)# 4) calcola la somma degli elementi alla posizione [,,1] dell'array_2sum(array_2[,,1])# 5) data la seguente lista, stampa il secondo elementomy_list <- list(        numeri = 1:5,        matrice = matrix(1:4, nrow = 2),        stringa = "Hello World")print(my_list[[2]])# 6) usando sempre la lista my_list, sostituisci la stringa con "finally the weekend"# stampa la lista modificata per verificaremy_list[[3]] <- "finally the weekend"my_list# o print(my_list)# 7) aggiungi un nuovo elemento dalla lista contenente un vettore logico di 3 elementimy_list$logico <- c(TRUE, FALSE, TRUE)# 8) data la seconda lista, concatena le due list con la funzione c() nell'oggetto combined_listmy_second_list <- list(        numero = 42,        stringa = "R is fun")combined_list <- c(my_list, my_second_list)# 9) data la lista seguente, delistala e salvala nell'oggetto vec1 # calcola la somma dei valori del vettore risultantemy_third_list <- list(        primo = c(1, 2, 3),        secondo = c(4, 5, 6),        terzo = c(7, 8, 9))vec1 <- unlist(my_third_list)sum(vec1)# 10) crea un vettore di fattori rappresentante i giorni della settimana# assicurati che i livelli del fattore siano ordinati correttamente da lunedì a domenica# stampa il fattore creatogiorni <- factor(c("lunedì", "martedì", "mercoledì", "giovedì", "venerdì", "sabato", "domenica"),                  levels = c("lunedì", "martedì", "mercoledì", "giovedì", "venerdì", "sabato", "domenica"))print(giorni)